import { React, useMemo } from 'react';
import { Button, Modal, Table, message, Image } from 'antd';
import { useMutation, useQuery } from 'react-query';
import { PostsServices } from '../../../services/posts.services'
import { ExclamationCircleOutlined } from "@ant-design/icons";
import { useNavigate } from 'react-router-dom';
import { AuthenticatedRoutesNames } from '../../../utilities/util.constant';
import { UtilServices } from '../../../utilities/util.services';
import PortalMainPage from '../../../components/PortalMainPage/PortalMainPage';

const { confirm } = Modal

function AdminPosts() {

    const navigate = useNavigate()

    const [messageApi, contextHolder] = message.useMessage()

    const { data: postData, isLoading: postLoading, refetch: postRefresh } =
        useQuery("PostData",
            () => PostsServices.getAllPosts())

    const getPostData = useMemo(
        () => postData?.data?.results,
        [postData?.data?.results])

    const { mutateAsync: deletePostRequest, isLoading: deletePostLoading }
        = useMutation(PostsServices.deletePostById)

    const deletePostHandler = (singleData) => {
        const { id: postId } = singleData
        confirm({
            title: 'Do you want to delete this post?',
            icon: <ExclamationCircleOutlined />,
            onOk() {
                deletePostRequest(postId, {
                    onSuccess: () => {
                        messageApi.success("Post is deleted sucessfully!");
                        postRefresh();
                    }
                })
            }
        })
    }

    const columns = [
        {
            title: 'ID',
            key: 'id',
            render: (singleData) => {
                return singleData.id
            },
        },
        {
            title: 'Post Title',
            key: 'post-title',
            render: (singleData) => {
                return singleData.post_title
            },
        },
        {
            title: 'Post Author',
            key: 'post-author',
            render: (singleData) => {
                return singleData.post_author
            },
        },
        {
            title: 'Image',
            key: 'image',
            render: (singleData) => {
                return (singleData.image) ? <Image width={200} src={singleData.image} /> :
                    <Image
                        width={200}
                        height={200}
                        src="error"
                        fallback="data:image/png;base64,"
                    />
            },
        },
        {
            title: 'created-at',
            key: 'created-at',
            render: (singleData) => {
                return UtilServices.convertDateToOurFormat(singleData.created_at)
            },
        },
        {
            title: 'updated-at',
            key: 'updated-at',
            render: (singleData) => {
                return UtilServices.convertDateToOurFormat(singleData.updated_at)
            },
        },
        {
            title: 'Edit',
            key: 'edit',
            render: (singleData) => {
                return <Button type='primary' onClick={
                    () => navigate(AuthenticatedRoutesNames.EditPosts.replace(':id',singleData.id))}>Edit</Button>
            },
        },
        {
            title: 'Delete',
            key: 'delete',
            render: (singleData) => {
                return <Button type='primary' danger onClick={() => deletePostHandler(singleData)}>Delete</Button>
            },
        },
    ];

    return (
        <div>
            {contextHolder}
            <PortalMainPage
                heading={"Posts"}
                addBtnText={"Add Post"}
                addBtn={AuthenticatedRoutesNames.AddPosts}
                columns={columns}
                dataSource={getPostData}
                loading={postLoading||deletePostLoading}
            />
        </div>
    )
}
export default AdminPosts;