import React, { useMemo } from 'react'
import { useMutation, useQuery } from 'react-query'
import { UserServices } from '../../../services/users.services'
import { ExclamationCircleOutlined } from "@ant-design/icons";
import PortalMainPage from '../../../components/PortalMainPage/PortalMainPage'
import { Button, Image, Modal,message } from 'antd'
import { UtilServices } from '../../../utilities/util.services'

function AdminUsers() {
    const [messageApi, contextHolder] = message.useMessage()
    const {
        data: userData,
        isLoading: usersLoading,
        refetch:refeatchUsers,
    } = useQuery("userdata", () => UserServices.getAllUsers())

    const getUsersData =
        useMemo(
            () => userData?.data?.results,
            [userData?.data?.results]
        )

        const { mutateAsync: deleteUserRequest, isLoading: deleteUserLoader } =
        useMutation(UserServices.deleteUserById);
    
      const deleteUserHendler = (userId) => {
        Modal.confirm({
          title: "Do you want to delete this user ?",
          icon: <ExclamationCircleOutlined />,
          onOk() {
            deleteUserRequest(userId, {
              onSuccess: () => {
                messageApi.success("user is deleted sucessfully!");
                refeatchUsers();
              },
            });
          },
        });
      };

    const columns = [
        {
            title: "ID",
            key: "userId",
            render: (singleUser) => {
                return singleUser.user_id
            }
        },
        {
            title: "User Name",
            key: "userName",
            render: (singleUser) => {
                return singleUser.username
            }
        },
        {
            title: "Email",
            key: "email",
            render: (singleUser) => {
                return singleUser.email
            }
        },
        {
            title: "Image",
            key: "image",
            render: (singleUser) => {
                return (singleUser.user_image) ? <Image width={200} src={singleUser.user_image} /> :
                    <Image
                        width={200}
                        height={200}
                        src="error"
                        fallback="data:image/png;base64,"
                    />
            }
        },
        {
            title: "Role",
            key: "role",
            render: (singleUser) => {
                return singleUser.user_role
            }
        },
        {
            title: "Created At",
            key: "createdat",
            render: (singleUser) => {
                return UtilServices.convertDateToOurFormat(singleUser.created_at)
            }
        },
        // {
        //     title:"Edit",
        //     key:"edit",
        //     render:()=>{
        //         return <Button type='primary'>Edit</Button>
        //     }
        // },
        {
            title: "Delete",
            key: "delete",
            render: (singleUser) => {
                return <Button type='primary' danger onClick={() => deleteUserHendler(singleUser.user_id)}>Delete</Button>
            }
        }
    ]

    return (
        <div>
        {contextHolder}
            <PortalMainPage
                heading={"Users"}
                //    addBtnText={"Add User"}
                //    addBtn={AuthenticatedRoutesNames.AddPosts}
                columns={columns}
                dataSource={getUsersData}
                loading={usersLoading||deleteUserLoader}
            />
        </div>
    )
}

export default AdminUsers